    private void btnCubeActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCubeActionPerformed
        //Calling and using the cubeNumber function to cube any input by the user.
        //The value is then displayed in the textbox.
        double answer;
        double number = Double.parseDouble(txtCube.getText());
        answer = cubeNumber(number);
        txtOutput.setText("" +number +" cubed is " +answer +".");
    }//GEN-LAST:event_btnCubeActionPerformed

