    private void btnAreaOfCircleActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAreaOfCircleActionPerformed
        //Calling and using the areaOfCircle function using the desired radius to find the area of a circle.
        //The area is then rounded and diplsayed in the textbox.
        double area;
        double radius = Double.parseDouble(txtRadius.getText());
        area = areaOfCircle(radius);
        txtOutput.setText("The area of a circle with a radius of " +radius +" is about " +(Math.round(area)) +".");
    }//GEN-LAST:event_btnAreaOfCircleActionPerformed

    private void btnAreaOfTriangleActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnAreaOfTriangleActionPerformed
        //Using the users choice of height and base, the area of a triangle will be found by calling the areaOfTrianglefunction.
        //The area is then rounded and diplayed in the textbox.
        double area;
        double base = Double.parseDouble(txtBase.getText());
        double height = Double.parseDouble(txtHeight.getText());
        area = areaOfTriangle(base,height);
        txtOutput.setText("The area of a triangle with height " +height +" and base " +base +" is about " +(Math.round(area)) +".");
    }//GEN-LAST:event_btnAreaOfTriangleActionPerformed

